import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import About from './pages/About';
import Login from './pages/Login';
import BrowseBooks from './pages/BrowseBooks';
import Profile from './pages/Profile';
import Register from './pages/Registration'; 
import { getToken, getRole, getUsername } from './utils/auth'
import './App.css';

const App = () => {
  const [userToken, setUserToken] = useState(getToken());
  const [userRole, setUserRole] = useState(getRole());
  const [username, setUsername] = useState(getUsername());

  useEffect(() => {
    const token = getToken();
    const role = getRole();
    const name = getUsername();
    setUserToken(token);
    setUserRole(role);
    setUsername(name);  // Update username here on initial load if it's available
  }, []);

  const handleLogin = (token, role, username) => {
    setUserToken(token);
    setUserRole(role);
    setUsername(username);  // Set username when login happens
  };

  const handleLogout = () => {
    setUserToken(null);
    setUserRole(null);
    setUsername(null);
  };

  return (
    <Router>
      <div className="App">
        <Navbar userToken={userToken} username={username} onLogout={handleLogout} />
        <Routes>
          <Route path="/" element={<Hero />} />
          <Route path="/about" element={<About />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route
            path="/browse-books"
            element={userToken ? <BrowseBooks /> : <Navigate to="/login" />}
          />
          <Route path="/register" element={<Register />} />
          <Route
            path="/profile"
            element={userToken ? <Profile /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
