import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import About from './pages/About';
import Login from './pages/Login';
import BrowseBooks from './pages/BrowseBooks';
import Profile from './pages/Profile';
import Register from './pages/Registration'; 
import { getToken, getRole, getUsername } from './utils/auth';
import './App.css';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userToken, setUserToken] = useState("");
  const [userRole, setUserRole] = useState("");
  const [username, setUsername] = useState("");

  // Fetch token, role, and username from local storage on initial load
  useEffect(() => {
    const token = getToken();
    const role = getRole();
    const name = getUsername();

    if (token) {
      setUserToken(token);
      setUserRole(role);
      setUsername(name);
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = (token, role, username) => {
    setUserToken(token);
    setUserRole(role);
    setUsername(username);
    setIsLoggedIn(true);  // Mark as logged in after successful login
  };

  const handleLogout = () => {
    setUserToken("");
    setUserRole("");
    setUsername("");
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div className="App">
        <Navbar uuserToken = {userToken} userRole={userRole} username={username} />
        <Routes>
          <Route path="/" element={<Hero />} />
          <Route path="/about" element={<About />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route
            path="/browse-books"
            element={isLoggedIn ? <BrowseBooks /> : <Navigate to="/login" />}
          />
          <Route path="/register" element={<Register />} />
          <Route
            path="/profile"
            element={isLoggedIn ? <Profile /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
