import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import About from './pages/About';
import Login from './pages/Login';
import BrowseBooks from './pages/BrowseBooks';
import Profile from './pages/Profile';
import Register from './pages/Registration'; 
import { getToken, getRole, getLoggedUsername } from './utils/auth';
import './App.css';

const App = () => {
  const [userToken, setUserToken] = useState(null);
  const [userRole, setUserRole] = useState("");
  const [username, setUsername] = useState("");

  useEffect(() => {
    // Check if a token is already in localStorage
    const token = getToken();
    if (token) {
      const role = getRole();
      const name = getLoggedUsername();
      setUserToken(token);
      setUserRole(role);
      setUsername(name);
    }
  }, []);

  const handleLogin = (token, role, username) => {
    setUserToken(token);
    setUserRole(role);
    setUsername(username);
  };

  const handleLogout = () => {
    setUserToken(null);
    setUserRole("");
    setUsername("");
    // Optionally remove token from localStorage
    localStorage.removeItem("token");
  };

  return (
    <Router>
      <div className="App">
        <Navbar />
        <Routes>
          <Route path="/" element={<Hero />} />
          <Route path="/about" element={<About />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route
            path="/browse-books"
            element={<BrowseBooks />}
          />
          <Route path="/register" element={<Register />} />
          <Route
            path="/profile"
            element={<Profile />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
